con:config.script:
import com.eviware.soapui.support.XmlHolder
def response = context.expand('${JDBC Request#ResponseAsXml}')
def xml=new XmlHolder(response)
def passvalue=xml.getNodeValue("//Row[1]/EMPLOYEEINFO.NAME")
log.info passvalue
def request=context.expand('${GetData#Request}')
def targetxml=new XmlHolder(request)
def PT=targetxml.setNodeValue("/typ:getEmployeeDetails//typ:employeeName",passvalue)
log.info PT.getXml()
